#include "VAXRegisterBankInfo.h"
#include "VAXInstrInfo.h" // For the register classes
#include "VAXSubtarget.h"
#include "llvm/CodeGen/MachineRegisterInfo.h"
#include "llvm/CodeGen/RegisterBank.h"
#include "llvm/CodeGen/RegisterBankInfo.h"
#include "llvm/CodeGen/TargetRegisterInfo.h"

#define GET_TARGET_REGBANK_IMPL
#include "VAXGenRegisterBank.inc"

using namespace llvm;

// FIXME: TableGen this.
// If it grows too much and TableGen still isn't ready to do the job, extract it
// into an VAXGenRegisterBankInfo.def (similar to AArch64).
namespace llvm {
namespace VAX {
enum PartialMappingIdx {
  PMI_GPR,
  PMI_Min = PMI_GPR,
};

const RegisterBankInfo::PartialMapping PartMappings[]{
    // GPR Partial Mapping
    {0, 32, GPRRegBank},
};

enum ValueMappingIdx {
  InvalidIdx = 0,
  GPR3OpsIdx = 1,
};

const RegisterBankInfo::ValueMapping ValueMappings[] = {
    // invalid
    {nullptr, 0},
    // 3 operands in GPRs
    {&PartMappings[PMI_GPR - PMI_Min], 1},
    {&PartMappings[PMI_GPR - PMI_Min], 1},
    {&PartMappings[PMI_GPR - PMI_Min], 1},

};
} // end namespace VAX
} // end namespace llvm

VAXRegisterBankInfo::VAXRegisterBankInfo(const TargetRegisterInfo &TRI)
    : VAXGenRegisterBankInfo() {}

const RegisterBankInfo::InstructionMapping &
VAXRegisterBankInfo::getInstrMapping(const MachineInstr &MI) const {
  auto Opc = MI.getOpcode();

  if (!isPreISelGenericOpcode(Opc)) {
    const InstructionMapping &Mapping = getInstrMappingImpl(MI);
    if (Mapping.isValid())
      return Mapping;
  }

  using namespace TargetOpcode;

  unsigned NumOperands = MI.getNumOperands();
  const ValueMapping *OperandsMapping = &VAX::ValueMappings[VAX::GPR3OpsIdx];

  switch (Opc) {
  case G_ADD:
  case G_SUB:
  case G_MUL:
  case G_UDIV:

  case G_XOR:
  case G_SHL:
  case G_ASHR:
  case G_ROTR:
  case G_ROTL:


  case G_LOAD:
  case G_STORE: {
    OperandsMapping = &VAX::ValueMappings[VAX::GPR3OpsIdx];
    break;
  }

  case G_CONSTANT:
  case G_FCONSTANT:
  case G_GLOBAL_VALUE:
  case G_FRAME_INDEX:
    OperandsMapping =
        getOperandsMapping({&VAX::ValueMappings[VAX::GPR3OpsIdx], nullptr});
    break;
  default:
    return getInvalidInstructionMapping();
  }

  return getInstructionMapping(DefaultMappingID, /*Cost=*/1, OperandsMapping,
                               NumOperands);
}
